    private static class ResourcePack implements ResourcePackInfo {

        private final File file;
        private final String name;
        private final UUID uuid;
        private URI uri;
        private String hash;

        public ResourcePack(@NotNull Plugin plugin, @NotNull File file) {
            this.file = file;
            this.name = plugin.getName();
            this.uuid = pluginUUIDs.get(plugin);
            resourcePacks.put(plugin.getName(), this);
            System.gc();
            try {
                this.hash = calculateSHA1(file);
            } catch (Exception e) {
                throw new RuntimeException(e);
            }
        }

        public @NotNull File getFile() {
            return this.file;
        }

        public @NotNull String getName() {
            return this.name;
        }

        @Override
        public @NotNull UUID id() {
            return this.uuid;
        }

        @Override
        public @NotNull URI uri() {
            return this.uri;
        }

        public ResourcePack forPlayer(boolean local) {
            return local ? forLocal() : forPublic();
        }

        public ResourcePack forLocal() {
            this.uri = URI.create(resPackUrl.formatted(localIP, webServerPort)).resolve("?file="+this.name);
            return this;
        }

        public ResourcePack forPublic() {
            this.uri = URI.create(resPackUrl.formatted(publicIP, webServerPort)).resolve("?file="+this.name);
            return this;
        }

        @Override
        public @NotNull String hash() {
            try {
                LOGGER.info(this.hash + ", " + calculateSHA1(this.file) + ", " + calculateSHA1(this.file));
                return calculateSHA1(this.file);
            } catch (Exception e) {
                throw new RuntimeException(e);
            }
        }
    }

    public void C_refreshResourcePack(Player player) {
        //if (!hasResourcePack()) return;
        boolean isLocal = Boolean.TRUE.equals(player.getPersistentDataContainer().get(new NamespacedKey(this, "isLocal"), PersistentDataType.BOOLEAN));
        String ip = isLocal ? "127.0.0.1" : publicIP; // isLocal ? localIP : publicIP;
        //LOGGER.warning(ip);
        packRequest.setPlayer(player.getUniqueId(), isLocal);
        player.sendResourcePacks(packRequest);
    }

