    public static void writePack(Plugin plugin, File file, List<ZipEntryData> entries) {
        if (entries.isEmpty())
            return;
        LOGGER.info("Zipping " + plugin.getName());
        try (ZipOutputStream zos = new ZipOutputStream(new FileOutputStream(file))) {
            for (ZipEntryData entryData : entries) {
                ZipEntry zipEntry = new ZipEntry(entryData.entry().getName());
                zipEntry.setTime(entryData.entry.getTime());
                zipEntry.setSize(entryData.entry.getSize());
                zipEntry.setMethod(entryData.entry.getMethod());
                zipEntry.setComment(entryData.entry.getComment());
                zipEntry.setCrc(entryData.entry.getCrc());
                zos.putNextEntry(zipEntry);
                zos.write(entryData.content());
                zos.closeEntry();
            }
            new ResourcePack(plugin, file);
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }

    ///
    /// @return Whether pack.mcmeta exists
    ///
    public static boolean addAssetsFromZipEntries(Consumer<ZipEntryData> entryConsumer, ZipFile zipFile) throws IOException {
        Enumeration<? extends ZipEntry> zipEntries = zipFile.entries();
        boolean hasPackMCMeta = false;
        while (zipEntries.hasMoreElements()) {
            ZipEntry zipEntry = zipEntries.nextElement();
            if (!zipEntry.getName().startsWith("assets/")) {
                if (zipEntry.isDirectory())
                    continue;
                if (!zipEntry.getName().equals("pack.mcmeta") && !zipEntry.getName().equals("pack.png"))
                    continue;
                hasPackMCMeta = true;
            }
            try (InputStream is = zipFile.getInputStream(zipEntry)) {
                byte[] content = is.readAllBytes();
                entryConsumer.accept(new ZipEntryData(zipEntry, content));
            }
        }
        return hasPackMCMeta;
    }

